INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509180, 'Itraconazole', 'Glecaprevir', 'Moderate', 'Coadministration with itraconazole may increase the plasma concentrations and risk of adverse effects of glecaprevir-pibrentasvir. The mechanism has not been clearly established, but may involve itraconazole-mediated inhibition of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or CYP450 3A4 isoenzyme.', 'DDInter', 1767317477, 'Caution and monitoring for adverse effects is recommended if itraconazole is used concomitantly with glecaprevir-pibrentasvir. A dosage adjustment should be considered whenever itraconazole is added to or withdrawn from therapy with these drugs.', 'Metabolism, Absorption', 'Caution and monitoring for adverse effects is recommended if itraconazole is used concomitantly with glecaprevir-pibrentasvir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
