INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509216, 'Glecaprevir', 'Rifampicin', 'Major', 'The plasma concentrations of glecaprevir may increase significantly during acute coadministration with rifampin, but decreases after chronic coadministration. Pibrentasvir plasma concentrations are not affected during acute coadministration with rifampin, but also decreases significantly after chronic coadministration. The proposed mechanism is rifampin inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of glecaprevir initially, followed by induction of P-glycoprotein-mediated intestinal efflux and CYP450 3A4-mediated metabolism of glecaprevir and pibrentasvir after chronic dosing.', 'DDInter', 1767317477, 'Concomitant use of glecaprevir and pibrentasvir with rifampin is considered contraindicated.', 'Metabolism', 'Concomitant use of glecaprevir and pibrentasvir with rifampin is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
