INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509820, 'Granisetron', 'Troglitazone', 'Moderate', 'Coadministration with troglitazone may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by troglitazone.', 'DDInter', 1767317477, 'Caution is advised if troglitazone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever troglitazone is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if troglitazone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509821, 'Granisetron', 'Tryptophan', 'Major', 'Concomitant use of 5-HT3 receptor antagonists with agents that possess or enhance serotonergic activity such as selective serotonin reuptake inhibitors (SSRIs), selective serotonin-norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), 5-HT1 receptor agonists (triptans), ergot alkaloids, phenylpiperidine opioids, bupropion, dextromethorphan, linezolid, lithium, St. John''s wort, tramadol, and tryptophan may potentiate the risk of serotonin syndrome.', 'DDInter', 1767317477, 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is warranted when initiating or increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life. For example, a 5-week washout period is typically recommended following use of fluoxetine and 2 weeks following use of MAOIs before administering another serotonergic agent. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.', 'Synergism', 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509822, 'Griseofulvin', 'Aminolevulinic acid', 'Major', 'Patients exposed to photosensitizing agents may be at increased risk of a phototoxic skin reaction (severe sunburn) during use of aminolevulinic acid.', 'DDInter', 1767317477, 'Photosensitizing agents (e.g., fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, St. John''s wort) and topical preparations containing aminolevulinic acid should generally not be administered for 24 hours before and after oral administration of aminolevulinic acid.', 'Synergism', 'Photosensitizing agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509823, 'Amprenavir', 'Griseofulvin', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509824, 'Anisindione', 'Griseofulvin', 'Moderate', 'Griseofulvin has been reported to increase warfarin requirements in some previously stabilized patients. The mechanism may be induction of the hepatic metabolism of warfarin. Similar effects may occur with other oral anticoagulants.', 'DDInter', 1767317477, 'Patients on oral anticoagulants should be monitored for alterations in INR whenever griseofulvin therapy is added, discontinued, or changed.', 'Metabolism', 'Patients on oral anticoagulants should be monitored for alterations in INR whenever griseofulvin therapy is added, discontinued, or changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509825, 'Griseofulvin', 'Bedaquiline', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to the major N-monodesmethyl metabolite, M2.', 'DDInter', 1767317477, 'Caution is advised when bedaquiline is used in combination with inducers of CYP450 3A4 due to the potential for reduced antimycobacterial effect.', 'Metabolism', 'Caution is advised when bedaquiline is used in combination with inducers of CYP450 3A4 due to the potential for reduced antimycobacterial effect.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509826, 'Griseofulvin', 'Boceprevir', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, which are metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is recommended if boceprevir or telaprevir is used in combination with a CYP450 3A4 inducer. Close clinical monitoring of the virologic response to the HCV NS3/4A protease inhibitor is recommended.', 'Metabolism', 'Caution is recommended if boceprevir or telaprevir is used in combination with a CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509827, 'Griseofulvin', 'Butabarbital', 'Moderate', 'Barbiturates may decrease serum griseofulvin levels. The mechanism may be related to decreased absorption of griseofulvin. Therapeutic failure has been reported. Phenobarbital and primidone are the only barbiturates specifically implicated in this interaction. However, other barbiturates may behave in a similar fashion.', 'DDInter', 1767317477, 'Patients should be monitored for reduced antifungal efficacy. It may be necessary to increase griseofulvin doses or use alternative therapeutic agents.', 'Absorption', 'Patients should be monitored for reduced antifungal efficacy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509828, 'Griseofulvin', 'Caspofungin', 'Moderate', 'Coadministration with inducers or mixed inducer/inhibitors of certain metabolic pathways may produce clinically meaningful reductions in the plasma concentrations of caspofungin.', 'DDInter', 1767317477, 'Caspofungin product labeling recommends that during coadministration with efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone or carbamazepine, an increase in the daily dosage to 70 mg (following the usual 70 mg loading dose) should be considered in patients who are not clinically responding.', 'Metabolism', 'Caspofungin product labeling recommends that during coadministration with efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone or carbamazepine, an increase in the daily dosage to 70 mg (following the usual 70 mg loading dose) should be considered in patients who are not clinically responding.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509829, 'Griseofulvin', 'Copanlisib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of copanlisib, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of copanlisib should be considered during coadministration with CYP450 3A4 and/or P-gp inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of copanlisib should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
