INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509850, 'Griseofulvin', 'Riociguat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers. Patients should be closely monitored, and the dosage of riociguat adjusted as necessary. Data are not available to guide dosing of riociguat when coadministered with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Alternative agents with no or minimal CYP450 3A4-inducing activity should be considered whenever possible.', 'Metabolism', 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509851, 'Griseofulvin', 'Telaprevir', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, which are metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is recommended if boceprevir or telaprevir is used in combination with a CYP450 3A4 inducer. Close clinical monitoring of the virologic response to the HCV NS3/4A protease inhibitor is recommended.', 'Metabolism', 'Caution is recommended if boceprevir or telaprevir is used in combination with a CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
