INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509900, 'Guanfacine', 'Methyldopa', 'Moderate', 'Use of guanfacine in combination with antihypertensive agents may result in additive pharmacodynamic effects such as hypotension and syncope.', 'DDInter', 1767317477, 'Caution is advised during coadministration of these agents. Dosage adjustments may be required and hemodynamic responses should be monitored during concomitant therapy. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'Caution is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509901, 'Guanfacine', 'Nafcillin', 'Major', 'Coadministration with inducers of CYP450 3A4 may significantly decrease the plasma concentrations of guanfacine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Close monitoring and dosage adjustment should be considered when guanfacine is administered with potent and moderate CYP450 3A4 inducers. For extended-release guanfacine, some manufacturers recommend increasing the dosage up to double the recommended level when initiating therapy in patients who are already receiving a CYP450 3A4 inducer. Further dosage adjustments may be required based on patient tolerance and response. If a CYP450 3A4 inducer is added to existing guanfacine therapy, the guanfacine dosage may be increased up to double the recommended level over 1 to 2 weeks. The dosage should be decreased to the recommended level over 1 to 2 weeks following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', 'Close monitoring and dosage adjustment should be considered when guanfacine is administered with potent and moderate CYP450 3A4 inducers.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509902, 'Guanfacine', 'Oxazepam', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509903, 'Guanfacine', 'Paraldehyde', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317477, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509904, 'Guanfacine', 'Penbutolol', 'Moderate', 'Use of central alpha-2 adrenergic agonists, such as clonidine or guanfacine, with beta-blockers may have synergistic pharmacodynamic effects resulting in AV block, bradycardia, and hypotension. In addition, sinus node dysfunction may also be enhanced. Abrupt withdrawal of an alpha-2 agonist or both the alpha-2 agonist and the beta-blocker may also result in rebound hypertensive effects. The proposed mechanism is related to increased catecholamine release after alpha-2 agonist withdrawal which, when combined with concurrent beta-blockade, results in unopposed vasoconstriction.', 'DDInter', 1767317477, 'Until more information is available, caution and close monitoring of blood pressure are recommended for patients receiving guanfacine with a beta-blocker. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some patients. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, headaches, or reduced or irregular heart rate.', 'Synergism', 'Until more information is available, caution and close monitoring of blood pressure are recommended for patients receiving guanfacine with a beta-blocker.', NULL, NULL, 'Moderate', 'pharmacodynamic');
