    ) VALUES (1510052, 'Haloperidol', 'Mazindol', 'Moderate', 'Butyrophenone neuroleptics may antagonize the pharmacologic effects of amphetamine, amphetamine derivatives, and other centrally-acting sympathomimetic agents (i.e., CNS stimulants). Conversely, these agents may diminish the neuroleptic efficacy of butyrophenones. The exact mechanism of interaction is unknown but may involve opposing effects on dopaminergic activity.', NULL, 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with a butyrophenone neuroleptic agent.', NULL, 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with a butyrophenone neuroleptic agent.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
