INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511194, 'Ifosfamide', 'Methotrimeprazine', 'Moderate', 'Centrally-acting agents such as antiemetics, sedatives, narcotics, or antihistamines may add to the neurotoxic effects of ifosfamide. Administration of ifosfamide can cause CNS toxicity, which necessitates careful monitoring of the patient. Neurologic manifestations include somnolence, confusion, hallucinations, blurred vision, psychotic behavior, extrapyramidal symptoms, urinary incontinence, seizures, and in some instances, coma. There have also been reports of peripheral neuropathy. Ifosfamide neurotoxicity may occur within a few hours to a few days after initial administration and typically resolves within 48 to 72 hours of ifosfamide discontinuation.', 'DDInter', 1767317477, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide. If ifosfamide-induced encephalopathy occurs, both ifosfamide and non-essential concomitant CNS agents should be discontinued. Supportive therapy should be initiated and maintained until complete resolution of neurotoxicity.', 'Synergism', 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511195, 'Ifosfamide', 'Morphine (liposomal)', 'Moderate', 'Centrally-acting agents such as antiemetics, sedatives, narcotics, or antihistamines may add to the neurotoxic effects of ifosfamide. Administration of ifosfamide can cause CNS toxicity, which necessitates careful monitoring of the patient. Neurologic manifestations include somnolence, confusion, hallucinations, blurred vision, psychotic behavior, extrapyramidal symptoms, urinary incontinence, seizures, and in some instances, coma. There have also been reports of peripheral neuropathy. Ifosfamide neurotoxicity may occur within a few hours to a few days after initial administration and typically resolves within 48 to 72 hours of ifosfamide discontinuation.', 'DDInter', 1767317477, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide. If ifosfamide-induced encephalopathy occurs, both ifosfamide and non-essential concomitant CNS agents should be discontinued. Supportive therapy should be initiated and maintained until complete resolution of neurotoxicity.', 'Synergism', 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511196, 'Ifosfamide', 'Mumps virus strain B level jeryl lynn live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 1767317477, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511197, 'Ifosfamide', 'Nalidixic acid', 'Major', 'Serious gastrointestinal toxicity such as hemorrhagic ulcerative colitis and intestinal necrosis has been reported with the concomitant use of nalidixic acid and melphalan.', 'DDInter', 1767317477, 'The concomitant use of nalidixic acid in patients undergoing therapy with melphalan or other related cancer chemotherapeutic alkylating agents is considered contraindicated.', 'Synergism', 'The concomitant use of nalidixic acid in patients undergoing therapy with melphalan or other related cancer chemotherapeutic alkylating agents is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511198, 'Ifosfamide', 'Naxitamab', 'Moderate', 'The concomitant or sequential administration of multiple antineoplastic agents may result in additive toxicities, particularly in the bone marrow and gastrointestinal tract.', 'DDInter', 1767317477, 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals. Dosing adjustments may be necessary. The manufacturers'' recommendations and institutional protocols for dosage, treatment regimens, monitoring, and management of toxicities should be consulted.', 'Synergism', 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511199, 'Ifosfamide', 'Netilmicin', 'Moderate', 'The concurrent or sequential use of two or more potentially nephrotoxic or neurotoxic drugs may increase the risk of toxicity. Patients who are elderly, debilitated, dehydrated, or who have preexisting renal dysfunction may have a higher risk of developing adverse reactions.', 'DDInter', 1767317477, 'Close clinical and laboratory monitoring of renal and neurologic function and drug serum concentrations (if clinically appropriate) is recommended.', 'Synergism', 'Close clinical and laboratory monitoring of renal and neurologic function and drug serum concentrations (if clinically appropriate) is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
