INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511237, 'Iloperidone', 'Amprenavir', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may increase the plasma concentrations of iloperidone, which is a substrate of the isoenzymes.', 'DDInter', 1767317477, 'The dosage of iloperidone should be reduced by one-half when administered concomitantly with potent CYP450 3A4 inhibitors (e.g., itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, clarithromycin, telithromycin) and/or potent CYP450 2D6 inhibitors (e.g., dacomitinib, fluoxetine, paroxetine, quinidine). Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Following discontinuation of the potent CYP450 3A4 or 2D6 inhibitor, the iloperidone dosage should be returned to the previous level.', 'Metabolism', 'The dosage of iloperidone should be reduced by one-half when administered concomitantly with potent CYP450 3A4 inhibitors (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511238, 'Amyl Nitrite', 'Iloperidone', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317477, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
