INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511455, 'Imatinib', 'Halazepam', 'Moderate', 'Coadministration with imatinib may increase the plasma concentrations of drugs that are substrates of CYP450 2C9, 2D6 and/or 3A4. The mechanism is decreased clearance due to inhibition of these isoenzymes by imatinib.', 'DDInter', 1767317477, 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever imatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511456, 'Imatinib', 'Halofantrine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of halofantrine, resulting in an increased risk of QT interval prolongation and ventricular arrhythmias. The mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of halofantrine. Halofantrine has been associated with QT interval prolongation, ventricular arrhythmias, and sudden death, even at recommended dosages.', 'DDInter', 1767317477, 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors. The manufacturer recommends performing an ECG before initiating halofantrine therapy and cardiac monitoring during and for 8 to 12 hours after completion of therapy.', 'Metabolism', 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
