INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1511517, 'Imipenem', 'Amifampridine', 'Moderate', 'The use of amifampridine is associated with a dose-related risk of seizures, and the risk may be further increased when coadministered with other drugs that can reduce the seizure threshold. Seizures have occurred in patients without a history of seizures taking amifampridine at recommended dosages and at various times after initiation of treatment.', NULL, 'Caution is advised if amifampridine is coadministered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with other risk factors for seizures (e.', NULL, 'Caution is advised if amifampridine is coadministered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with other risk factors for seizures (e.g., head trauma; brain tumor; severe hepatic cirrhosis; metabolic disorders; CNS infections; excessive use of alcohol or sedatives; addiction to opiates, cocaine, or stimulants; diabetes treated with oral hypoglycemic agents or insulin). Consider discontinuation or dose reduction of amifampridine in patients who experience a seizure during treatment. Use of amifampridine is contraindicated in patients with a history of seizures.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
