        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1511521, 'Imipenem', 'Vibrio cholerae CVD 103-HgR strain live antigen (live)', 'Major', 'INTERVAL: The use of live, attenuated cholera vaccine with systemic antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Vibrio cholerae, thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', NULL, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
