        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1511525, 'Imipenem', 'Dyphylline', 'Moderate', 'Some case reports suggest an increased risk of seizures when theophylline and imipenem are coadministered. The mechanism is unknown. Patients receiving large doses of either drug and/or with renal dysfunction may be at a higher risk.', NULL, 'Dosing should be individualized and patients should be closely monitored for signs of neurotoxicity (e.', NULL, 'Dosing should be individualized and patients should be closely monitored for signs of neurotoxicity (e.g., confusion, seizures, myoclonic activity).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
