        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1511541, 'Imipenem', 'Probenecid', 'Minor', 'Coadministration with probenecid may increase the plasma concentrations of carbapenem antibiotics. The mechanism involves competitive inhibition of carbapenem renal excretion via active tubular secretion. Significant pharmacokinetic alterations have been reported for some carbapenems such as doripenem and meropenem, but not for others like ertapenem and imipenem.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
