        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1511554, 'Anisindione', 'Imipramine', 'Moderate', 'Some reports suggest that tricyclic antidepressants may increase or decrease the anticoagulant effect of warfarin by an unknown mechanism. Similar effects may occur with other oral anticoagulants.', NULL, 'Until more information is available, it may be advisable to monitor the International Normalized Ratio (INR) or prothrombin time and clinical response if these agents are administered concomitantly.', NULL, 'Until more information is available, it may be advisable to monitor the International Normalized Ratio (INR) or prothrombin time and clinical response if these agents are administered concomitantly. Patients should be advised to promptly report possible signs of bleeding (e.g., pain, swelling, headache, dizziness, weakness, nosebleeds, bleeding of gums from brushing, unusual bruising, red or brown urine, or red or black stools) or clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
