    ) VALUES (1511555, 'Imipramine', 'Arbutamine', 'Major', 'Anticholinergic effects, tachycardia and conduction abnormalities associated with tricyclic antidepressant therapy may increase the risk for cardiotoxicity in patients receiving arbutamine for diagnostic purposes.', NULL, 'Arbutamine should not be administered to patients also receiving tricyclic antidepressants.', NULL, 'Arbutamine should not be administered to patients also receiving tricyclic antidepressants.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
