    ) VALUES (1511792, 'Indapamide', 'Ginkgo biloba', 'Moderate', 'An isolated case report describes an elderly patient with hypertension whose blood pressure increased following the addition of ginkgo while receiving a thiazide diuretic. The mechanism of interaction is unknown.', NULL, 'In general, patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'In general, patients should consult a healthcare provider before taking any herbal or alternative medicine. Caution may be appropriate when combining ginkgo products with thiazide diuretics, especially in the elderly. Blood pressure should be monitored regularly.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
