INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511873, 'Indinavir', 'Flurazepam', 'Moderate', 'Indinavir may interfere with the metabolism of benzodiazepines. The mechanism is competitive inhibition of the CYP450 3A4 enzyme. The accumulation of benzodiazepines is associated with potentially serious and/or life-threatening events (e.g., prolonged sedation, respiratory depression).', 'DDInter', 1767317477, 'Patients receiving benzodiazepines should be monitored for excessive pharmacological effects such as sedation. Lorazepam, temazepam and oxazepam are metabolized by glucuronidation and are not expected to be affected by indinavir.', 'Metabolism', 'Patients receiving benzodiazepines should be monitored for excessive pharmacological effects such as sedation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511874, 'Indinavir', 'Garlic', 'Major', 'Coadministration with garlic supplements may decrease the plasma concentrations of saquinavir and possibly other protease inhibitors (PIs). The exact mechanism is unknown but may involve induction of CYP450 3A4 metabolism and/or P-gycloprotein transport in the intestine by certain component(s) of garlic.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, garlic supplements should preferably be avoided in patients treated with PIs, particularly if they are using saquinavir as the sole PI in their antiretroviral regimen. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, garlic supplements should preferably be avoided in patients treated with PIs, particularly if they are using saquinavir as the sole PI in their antiretroviral regimen.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511875, 'Indinavir', 'Grepafloxacin', 'Moderate', 'Based on in vitro inhibition data, coadministration with grepafloxacin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by grepafloxacin.', 'DDInter', 1767317477, 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever grepafloxacin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
