INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511904, 'Indinavir', 'St. John''s Wort', 'Major', 'Coadministration with St. John''s wort may significantly reduce the plasma concentrations of HIV protease inhibitors and result in a potential loss of virologic response. The mechanism is induction of CYP450 3A4 metabolism by constituents of St. John''s wort.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of HIV protease inhibitors with St. John''s wort is considered contraindicated. The enzyme induction may last for up to 2 weeks after the last dose of St. John''s wort. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of HIV protease inhibitors with St.', NULL, NULL, 'Major', 'pharmacodynamic');
