INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511930, 'Indomethacin', 'Diflunisal', 'Major', 'Concomitant administration of diflunisal may increase serum indomethacin concentrations by up to two- to threefold and increase the risk of toxicity such as gastrointestinal and central nervous system side effects. The mechanism is unknown, but may involve competition for the same metabolic and secretory pathways, and/or inhibition of glucuronidation by diflunisal. The renal clearance of indomethacin and its conjugate are also significantly reduced. Fatal gastrointestinal hemorrhage has been reported.', 'DDInter', 1767317477, 'The manufacturer recommends that this combination should be avoided.', 'Metabolism, Excretion', 'The manufacturer recommends that this combination should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1511931, 'Indomethacin', 'Drotrecogin alfa', 'Major', 'Coadministration of drotrecogin alfa and other drugs that interfere with coagulation or platelet function may potentiate the risk of bleeding complications. Drotrecogin alfa inactivates blood clotting factors Va and VIIIa and may prolong the activated partial thromboplastin time (APTT).', 'DDInter', 1767317477, 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia. Close clinical and laboratory monitoring for bleeding complications is recommended if concurrent therapy is required. Drotrecogin alfa should be discontinued immediately if clinically significant bleeding occurs.', 'Synergism', 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia.', NULL, NULL, 'Major', 'pharmacodynamic');
