INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512185, 'Dolutegravir', 'Zinc sulfate', 'Major', 'INTERVAL: Coadministration with medications or supplements containing polyvalent cations such as zinc may decrease the plasma concentrations of integrase strand transfer inhibitors (INSTIs). The proposed mechanism involves chelation of the INSTI by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 1767317477, 'If concomitant use is required, monitor for virologic efficacy and administer the INSTI at least 2 hours before or 6 hours after products containing polyvalent cations such as zinc.', 'Absorption', 'If concomitant use is required, monitor for virologic efficacy and administer the INSTI at least 2 hours before or 6 hours after products containing polyvalent cations such as zinc.', NULL, NULL, 'Major', 'pharmacodynamic');
