INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512188, 'Iodide I-123', 'Sodium iodide', 'Moderate', 'INTERVAL: Iodine-containing products may affect the pharmacokinetics, pharmacodynamics, and/or diagnostic results of radioiodides. These effects may last up to 8 weeks.', 'DDInter', 1767317477, 'Iodine preparations including expectorants, vitamins, topicals, and intravascular iodine-containing contrast media should be withheld for at least 2 to 4 weeks and possibly longer, and cholecystographic agents should be withheld for more than 4 weeks, prior to administration of sodium iodide I-131 or I-123.', 'Others', 'Iodine preparations including expectorants, vitamins, topicals, and intravascular iodine-containing contrast media should be withheld for at least 2 to 4 weeks and possibly longer, and cholecystographic agents should be withheld for more than 4 weeks, prior to administration of sodium iodide I-131 or I-123.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512189, 'Sparfloxacin', 'Selenium', 'Moderate', 'INTERVAL: Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics. Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc. The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 1767317477, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', 'Absorption', 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512190, 'Riociguat', 'Abacavir', 'Moderate', 'Coadministration with inhibitors of CYP450 1A1 may theoretically increase the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme to its main active metabolite (1/10th to 1/3rd the pharmacologic activity of riociguat). Increased levels of riociguat may increase the risk for hypotension.', 'DDInter', 1767317477, 'Caution is advised if riociguat is prescribed in combination with an inhibitor of CYP450 1A1. Blood pressure should be monitored, and a dose reduction of riociguat should be considered if necessary.', 'Metabolism', 'Caution is advised if riociguat is prescribed in combination with an inhibitor of CYP450 1A1.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512191, 'Aluminum hydroxide', 'Riociguat', 'Moderate', 'INTERVAL: Concurrent administration of riociguat with antacids may reduce its gastrointestinal absorption. Riociguat exhibits pH-dependent solubility, with increased solubility at lower pH.', 'DDInter', 1767317477, 'Antacids should not be taken within 1 hour of riociguat administration.', 'Absorption', 'Antacids should not be taken within 1 hour of riociguat administration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512192, 'Aminoglutethimide', 'Riociguat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers. Patients should be closely monitored, and the dosage of riociguat adjusted as necessary. Data are not available to guide dosing of riociguat when coadministered with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Alternative agents with no or minimal CYP450 3A4-inducing activity should be considered whenever possible.', 'Metabolism', 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512193, 'Aminophylline', 'Riociguat', 'Major', 'Coadministration of riociguat with phosphodiesterase (PDE) inhibitors may cause significant hypotension. The mechanism involves peripheral vasodilation secondary to enhanced levels of cyclic guanosine monophosphate (cGMP) in vascular smooth muscle cells, as PDE inhibitors prevent degradation of cGMP while riociguat promotes its synthesis by stimulating soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cGMP. Riociguat does not affect the pharmacokinetics of sildenafil.', 'DDInter', 1767317477, 'Concomitant use of riociguat with PDE inhibitors, including specific PDE-5 inhibitors (e.g., avanafil, sildenafil, tadalafil, vardenafil) or nonspecific PDE inhibitors (e.g., dipyridamole, theophylline), is considered contraindicated. Riociguat should be discontinued at least 24 hours before administering a PDE-5 inhibitor, and avoidance of riociguat administration within 24 hours after sildenafil or within 48 hours after tadalafil is recommended. Limited data exists for other PDE inhibitors.', 'Others', 'Concomitant use of riociguat with PDE inhibitors, including specific PDE-5 inhibitors (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512194, 'Amprenavir', 'Riociguat', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 or moderate to potent inhibitors of P-glycoprotein (P-gp) or breast cancer resistant protein (BCRP) may increase the plasma concentrations of riociguat, which is partially metabolized by CYP450 3A and also a substrate of the P-gp and BCRP efflux transporters. Increased levels of riociguat may increase the risk for hypotension.', 'DDInter', 1767317477, 'Caution is advised when riociguat is prescribed with potent CYP450 3A4 inhibitors or moderate to potent P-gp or BCRP inhibitors. Patients should be monitored for signs and symptoms of hypotension, and the dosage of riociguat adjusted if necessary.', 'Metabolism', 'Caution is advised when riociguat is prescribed with potent CYP450 3A4 inhibitors or moderate to potent P-gp or BCRP inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512195, 'Amyl Nitrite', 'Riociguat', 'Major', 'Coadministration of riociguat with organic nitrates or nitric oxide donors may cause significant hypotension, syncope, and myocardial ischemia. Riociguat stimulates soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cyclic guanosine monophosphate (cGMP). The resultant accumulation of cGMP in vascular smooth muscle cells leads to peripheral vasodilation.', 'DDInter', 1767317477, 'Concomitant use of riociguat with organic nitrates (e.g., nitroglycerin) or nitric oxide donors (e.g., amyl nitrite) in any form is considered contraindicated.', 'Synergism', 'Concomitant use of riociguat with organic nitrates (e.', NULL, NULL, 'Major', 'pharmacodynamic');
