INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512210, 'Riociguat', 'Ketoconazole', 'Major', 'Coadministration with drugs that are both potent multi-pathway CYP450 and P-glycoprotein/breast cancer resistant protein (P-gp/BCRP) inhibitors may significantly increase the plasma concentrations of riociguat, which is primarily metabolized by CYP450 1A1, 3A, 2C8 and 2J2, and also a substrate of the P-gp/BCRP efflux transporter. Increased levels of riociguat may increase the risk for hypotension.', 'DDInter', 1767317477, 'Caution is advised if riociguat is prescribed in combination with potent, dual multi-pathway CYP450 and P-gp/BCRP inhibitors such as itraconazole, ketoconazole, and some HIV protease inhibitors. Some authorities recommend avoiding concomitant use of riociguat during and for 2 weeks after treatment with itraconazole. However, if concomitant use is necessary, a starting dosage of riociguat 0.5 mg three times a day should be considered, and patients should be monitored for signs and symptoms of hypotension.', 'Metabolism', 'Caution is advised if riociguat is prescribed in combination with potent, dual multi-pathway CYP450 and P-gp/BCRP inhibitors such as itraconazole, ketoconazole, and some HIV protease inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512211, 'Riociguat', 'Lomitapide', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 or moderate to potent inhibitors of P-glycoprotein (P-gp) or breast cancer resistant protein (BCRP) may increase the plasma concentrations of riociguat, which is partially metabolized by CYP450 3A and also a substrate of the P-gp and BCRP efflux transporters. Increased levels of riociguat may increase the risk for hypotension.', 'DDInter', 1767317477, 'Caution is advised when riociguat is prescribed with potent CYP450 3A4 inhibitors or moderate to potent P-gp or BCRP inhibitors. Patients should be monitored for signs and symptoms of hypotension, and the dosage of riociguat adjusted if necessary.', 'Metabolism', 'Caution is advised when riociguat is prescribed with potent CYP450 3A4 inhibitors or moderate to potent P-gp or BCRP inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
