INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512265, 'Deserpidine', 'Tetryzoline (ophthalmic)', 'Moderate', 'Postganglionic adrenergic blocking agents may potentiate the pharmacologic effects of direct-acting sympathomimetic amines, while the hypotensive effect of the former may be diminished. Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings, thus they may sensitize adrenergic receptors to direct-acting sympathomimetics.', 'DDInter', 1767317477, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension. Conversely, alternatives to postganglionic adrenergic blocking agents should be considered in patients treated with sympathomimetic amines. If the combination is used, blood pressure and heart rate should be closely monitored.', 'Synergism', 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512266, 'Ergometrine', 'Tetryzoline (ophthalmic)', 'Major', 'Additive or synergistic increases in blood pressure and/or ischemic response may occur when ergot alkaloids are combined with peripheral or central vasoconstrictors. Ergot alkaloids produce arterial vasoconstriction by stimulating alpha-adrenergic and serotonin receptors and inhibiting endothelial-derived relaxation factor release.', 'DDInter', 1767317477, 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents. If concomitant use is required, patients should be monitored for excessive vasoconstriction and have vital signs measured regularly. Patients should also be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents.', NULL, NULL, 'Major', 'pharmacodynamic');
