INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512277, 'Amprenavir', 'Acetohexamide', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317477, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512278, 'Amprenavir', 'Aluminum hydroxide', 'Moderate', 'INTERVAL: Concurrent administration of antacids or other agents with acid-neutralizing effects may decrease the oral bioavailability of amprenavir and reduce its concentrations in plasma. Amprenavir solubility decreases with increasing pH, thus reduction in gastric acidity may interfere with dissolution of the drug. Subtherapeutic antiretroviral drug levels may lead to reduced viral susceptibility and development of resistance. In contrast, the prodrug fosamprenavir appears to be less affected by antacids.', 'DDInter', 1767317477, 'Amprenavir should be administered at least 1 hour before or after antacids or other oral preparations containing antacids (e.g., didanosine buffered tablets or pediatric oral solution).', 'Absorption', 'Amprenavir should be administered at least 1 hour before or after antacids or other oral preparations containing antacids (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512279, 'Amprenavir', 'Aminoglutethimide', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512280, 'Anisindione', 'Amprenavir', 'Moderate', 'Coadministration with protease inhibitors may alter the plasma concentrations and/or pharmacologic effects of oral anticoagulants. The exact mechanism of interaction is unknown.', 'DDInter', 1767317477, 'Caution is advised if oral anticoagulants must be used with protease inhibitors. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of the protease inhibitor(s) in patients who are stabilized on their anticoagulant regimen.', 'Others', 'Caution is advised if oral anticoagulants must be used with protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512281, 'Amprenavir', 'Astemizole', 'Major', 'Coadministration with protease inhibitors (PIs), particularly ritonavir, may significantly increase the plasma concentrations of astemizole and terfenadine. The mechanism is PI inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of both astemizole and terfenadine. Although the interaction has not been specifically studied with any of the available PIs, high plasma levels of astemizole and terfenadine (e.g., due to overdose or interaction with other potent 3A4 inhibitors such as macrolide antibiotics and azole antifungal agents) have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death.', 'DDInter', 1767317477, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, use of these agents in patients treated with protease inhibitors is considered contraindicated. Loratadine, cetirizine, or fexofenadine may be safer alternatives during therapy with PIs.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, use of these agents in patients treated with protease inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512282, 'Amprenavir', 'Bedaquiline', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to its less active N-monodesmethyl metabolite, M2.', 'DDInter', 1767317477, 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk. Appropriate clinical monitoring for bedaquiline-related adverse reactions is recommended if coadministration is required. ECG, serum electrolytes (potassium, magnesium, calcium), and liver function tests (ALT, AST, alkaline phosphatase, bilirubin) should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration. Bedaquiline should be interrupted in patients who develop clinically significant ventricular arrhythmia or a QTcF interval greater than 500 msec confirmed by repeat ECG. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512283, 'Amprenavir', 'Benznidazole', 'Major', 'Amprenavir oral solution contains a large amount of propylene glycol, which may produce a toxic reaction when coadministered with disulfiram or other drugs that may be capable of inhibiting aldehyde dehydrogenase (ALDH) such as metronidazole. Inhibition of ALDH results in accumulation of acetaldehyde during the metabolism of propylene glycol. High levels of acetaldehyde can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion. Severe reactions may result in respiratory depression, cardiovascular collapse, arrhythmia, myocardial infarction, acute congestive heart failure, unconsciousness, convulsions, and death. The interaction is well established for disulfiram.', 'DDInter', 1767317477, 'The use of amprenavir oral solution with disulfiram or metronidazole (oral, intravenous, and probably vaginal preparations) is considered contraindicated. Given their structural similarities to metronidazole, the same precaution may be applicable to other nitroimidazoles such as benznidazole and tinidazole, although clinical data are lacking.', 'Metabolism', 'The use of amprenavir oral solution with disulfiram or metronidazole (oral, intravenous, and probably vaginal preparations) is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512284, 'Amprenavir', 'Bepridil', 'Major', 'Coadministration with amprenavir or its prodrug, fosamprenavir, may significantly increase the plasma concentrations of antiarrhythmic agents that are primarily metabolized by CYP450 3A4 such as amiodarone, bepridil, systemic lidocaine, and quinidine. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by amprenavir. The interaction has not been specifically studied, but could conceivably lead to serious and/or life-threatening reactions including cardiac arrhythmias and other toxicities if levels are substantially increased. The use of amiodarone, bepridil, and quinidine has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', 'DDInter', 1767317477, 'Caution is advised if amprenavir or fosamprenavir must be used with antiarrhythmic agents that are substrates of CYP450 3A4. Pharmacologic response and plasma antiarrhythmic drug levels should be monitored more closely whenever amprenavir or fosamprenavir is added to or withdrawn from therapy, and the antiarrhythmic dosage adjusted as necessary. Concomitant use with amiodarone, bepridil, lidocaine, or quinidine is specifically contraindicated according to Canadian and European labeling.', 'Metabolism', 'Caution is advised if amprenavir or fosamprenavir must be used with antiarrhythmic agents that are substrates of CYP450 3A4.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512285, 'Amprenavir', 'Berotralstat', 'Major', 'Coadministration with inducers of P-glycoprotein (P-gp) may significantly decrease the plasma concentrations and pharmacologic effects of berotralstat, a substrate of this efflux transporter.', 'DDInter', 1767317477, 'Due to the potential for diminished pharmacologic effects of berotralstat, its concomitant use with P-gp inducers, including but not limited to rifampin and St. John''s wort, should be avoided. Alternative therapeutic agents with less P-gp induction potential should be considered whenever possible.', 'Excretion', 'Due to the potential for diminished pharmacologic effects of berotralstat, its concomitant use with P-gp inducers, including but not limited to rifampin and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512286, 'Amprenavir', 'Boceprevir', 'Moderate', 'Coadministration of boceprevir and some ritonavir-boosted protease inhibitor regimens has been associated with decreased plasma concentrations of both boceprevir and the protease inhibitors. The mechanism of interaction has not been described. Data are currently unavailable regarding a potential interaction between boceprevir and other protease inhibitors, whether alone or ritonavir-boosted.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, caution is advised if boceprevir is used in combination with ritonavir-boosted protease inhibitor regimens. Patients coinfected with chronic HCV and HIV who have been started on one of these combinations should be closely monitored for treatment response and potential HCV and HIV virologic rebound. The safety and efficacy of boceprevir has not been established in the HCV/HIV coinfected population.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, caution is advised if boceprevir is used in combination with ritonavir-boosted protease inhibitor regimens.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512287, 'Amprenavir', 'Butabarbital', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512288, 'Amprenavir', 'Cerivastatin', 'Major', 'Protease inhibitors (PIs), particularly ritonavir, may significantly increase the plasma concentrations of certain HMG-CoA reductase inhibitors. The mechanism is PI inhibition of CYP450 3A4 metabolism. High levels of HMG-CoA reductase inhibitory activity in plasma are associated with an increased risk of musculoskeletal toxicity, including myopathy and rhabdomyolysis, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', 'DDInter', 1767317477, 'Simvastatin or lovastatin are considered contraindicated in patients treated with tipranavir and/or ritonavir due to the potential for severe interaction. Red yeast rice (which contains lovastatin) should also be avoided in patients treated with PIs. Fluvastatin and pravastatin are probably safer alternatives, since they are not metabolized by CYP450 3A4. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Simvastatin or lovastatin are considered contraindicated in patients treated with tipranavir and/or ritonavir due to the potential for severe interaction.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512289, 'Amprenavir', 'Cisapride', 'Major', 'Coadministration with protease inhibitors (PIs), particularly ritonavir, may significantly increase the plasma concentrations of cisapride. The mechanism is PI inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of cisapride.', 'DDInter', 1767317477, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, concomitant use with protease inhibitors is considered contraindicated.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, concomitant use with protease inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512290, 'Amprenavir', 'Clotrimazole', 'Minor', 'The coadministration with drugs that are inhibitors of the CYP450 3A4 enzymatic pathway may increase the plasma concentrations of amprenavir, which is metabolized by CYP450 3A4. The possibility of prolonged and/or increased pharmacologic effects of amprenavir should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience nausea, vomiting, diarrhea, or a rash.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512291, 'Amprenavir', 'Copanlisib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of copanlisib, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317477, 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors. Patients should be monitored for potentially increased adverse effects such as nausea, vomiting, diarrhea, stomatitis, hyperglycemia, hypertension, noninfectious pneumonitis, cutaneous reactions (e.g., exfoliative dermatitis, maculopapular rash), anemia, neutropenia, thrombocytopenia, and infections. Dosage adjustments for copanlisib may be necessary and should be done in accordance with the product labeling.', 'Metabolism', 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512292, 'Amprenavir', 'Dexamethasone (nasal)', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of corticosteroids, which are primarily metabolized by the isoenzyme. The interaction has been reported with potent inhibitors such as clarithromycin, erythromycin, itraconazole, nefazodone, cobicistat, and ritonavir during concomitant use of various corticosteroids, including inhaled, nasal, and ophthalmic formulations. Systemic corticosteroid adverse effects may occur following intensive or long-term continuous ophthalmic corticosteroid therapy. Cushing''s syndrome and adrenal insufficiency have been attributed to the interaction.', 'DDInter', 1767317477, 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors. When indicated for intranasal or inhalational use, alternative corticosteroids such as beclomethasone, which is less dependent on CYP450 3A4 metabolism, should be considered, particularly if long term treatment is required. Patients should be monitored for signs and symptoms of hypercorticism such as acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Following extensive use with a potent CYP450 3A4 inhibitor, a progressive dosage reduction may be required over a longer period if the corticosteroid is to be withdrawn from therapy, as there may be a significant risk of adrenal suppression.', 'Metabolism', 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512293, 'Amprenavir', 'Dicoumarol', 'Moderate', 'Coadministration with protease inhibitors may alter the plasma concentrations and/or pharmacologic effects of oral anticoagulants. The exact mechanism of interaction is unknown.', 'DDInter', 1767317477, 'Caution is advised if oral anticoagulants must be used with protease inhibitors. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of the protease inhibitor(s) in patients who are stabilized on their anticoagulant regimen.', 'Others', 'Caution is advised if oral anticoagulants must be used with protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512294, 'Amprenavir', 'Echinacea', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 1767317477, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512295, 'Amprenavir', 'Ergometrine', 'Major', 'Coadministration with protease inhibitors (PIs), particularly ritonavir, may significantly increase the plasma concentrations of ergot derivatives. The mechanism is PI inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ergotamine and related drugs.', 'DDInter', 1767317477, 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with protease inhibitors is considered contraindicated.', 'Metabolism', 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with protease inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512296, 'Amprenavir', 'Flurazepam', 'Moderate', 'Coadministration with amprenavir or its prodrug, fosamprenavir, may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by amprenavir.', 'DDInter', 1767317477, 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever amprenavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512297, 'Amprenavir', 'Garlic', 'Major', 'Coadministration with garlic supplements may decrease the plasma concentrations of saquinavir and possibly other protease inhibitors (PIs). The exact mechanism is unknown but may involve induction of CYP450 3A4 metabolism and/or P-gycloprotein transport in the intestine by certain component(s) of garlic.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, garlic supplements should preferably be avoided in patients treated with PIs, particularly if they are using saquinavir as the sole PI in their antiretroviral regimen. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, garlic supplements should preferably be avoided in patients treated with PIs, particularly if they are using saquinavir as the sole PI in their antiretroviral regimen.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512298, 'Amprenavir', 'Grepafloxacin', 'Moderate', 'Based on in vitro inhibition data, coadministration with grepafloxacin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by grepafloxacin.', 'DDInter', 1767317477, 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever grepafloxacin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512299, 'Amprenavir', 'Halazepam', 'Moderate', 'Coadministration with amprenavir or its prodrug, fosamprenavir, may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by amprenavir.', 'DDInter', 1767317477, 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever amprenavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512300, 'Amprenavir', 'Halofantrine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of halofantrine, resulting in an increased risk of QT interval prolongation and ventricular arrhythmias. The mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of halofantrine. Halofantrine has been associated with QT interval prolongation, ventricular arrhythmias, and sudden death, even at recommended dosages.', 'DDInter', 1767317477, 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors. The manufacturer recommends performing an ECG before initiating halofantrine therapy and cardiac monitoring during and for 8 to 12 hours after completion of therapy.', 'Metabolism', 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512301, 'Insulin human', 'Amprenavir', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317477, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512302, 'Insulin aspart (aspart protamine)', 'Amprenavir', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317477, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
