INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1512317, 'Amprenavir', 'Paclitaxel (protein-bound)', 'Major', 'Coadministration with certain antiretroviral agents such as protease inhibitors and delavirdine may increase the plasma concentrations and pharmacologic effects of paclitaxel. The proposed mechanism is inhibition of the CYP450 3A4-mediated metabolism of paclitaxel.', 'DDInter', 1767317477, 'Caution is advised if paclitaxel is required in patients receiving protease inhibitors and/or delavirdine. A lower initial dosage of paclitaxel may be appropriate. Patients should be closely monitored for the development of dose-related paclitaxel toxicity such as myelosuppression, stomatitis, arthralgia, myalgia, visual disturbances and peripheral neuropathy, and the paclitaxel dosage further adjusted as necessary. Use of repaglinide with the fixed combination of atazanavir-cobicistat is not recommended.', 'Metabolism', 'Caution is advised if paclitaxel is required in patients receiving protease inhibitors and/or delavirdine.', NULL, NULL, 'Major', 'pharmacodynamic');
