    ) VALUES (1512434, 'Aminophylline', 'Aminoglutethimide', 'Moderate', 'Aminoglutethimide may significantly reduce theophylline serum concentrations by increasing theophylline clearance. The mechanism is induction of CYP450 3A4 hepatic microsomal enzymes.', NULL, 'The patient should be monitored for clinical and laboratory evidence of altered theophylline response when aminoglutethimide is added, discontinued, or the dosage is changed.', NULL, 'The patient should be monitored for clinical and laboratory evidence of altered theophylline response when aminoglutethimide is added, discontinued, or the dosage is changed. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms during concurrent therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
