    ) VALUES (1512453, 'Isoniazid', 'Aminophylline', 'Moderate', 'Theophylline serum concentrations may be gradually increased by concomitant isoniazid administration. The mechanism is thought to involve isoniazid-induced inhibition of the CYP450 3A4 and 1A2 hepatic metabolism of theophylline.', NULL, 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended when isoniazid is added.', NULL, 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended when isoniazid is added. Patients should be advised to report any signs of theophylline toxicity to their caregiver, including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
