        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1512469, 'Aminophylline', 'Rapacuronium', 'Moderate', 'Methylxanthines may antagonize the effects of neuromuscular blockers. The mechanism is unknown. In patients stabilized on aminophylline, resistance to pancuronium has been reported.', NULL, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', NULL, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
