INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513044, 'Bismuth subgallate', 'Levacetylmethadol', 'Moderate', 'Coadministration of narcotic analgesics with antidiarrheal agents may have additive central nervous system (CNS) and gastrointestinal (GI) system effects, and increase the risk of severe constipation or paralytic ileus and CNS depression.', 'DDInter', 1767317477, 'If concomitant use cannot be avoided, patients should be monitored for potentially excessive or prolonged CNS depression and constipation.', 'Synergism', 'If concomitant use cannot be avoided, patients should be monitored for potentially excessive or prolonged CNS depression and constipation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
