INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513096, 'Brexucabtagene autoleucel', 'Triamcinolone', 'Major', 'Prophylactic use of systemic corticosteroids prior to the administration of brexucabtagene autoleucel may interfere with the therapeutic effects of brexucabtagene autoleucel. The mechanism for this interaction has not been delineated. Administration of systemic corticosteroids in an effort to manage brexucabtagene autoleucel-related toxicities does not affect the expansion and persistence of cells.', 'DDInter', 1767317477, 'The prophylactic use of systemic corticosteroids prior to the administration of brexucabtagene autoleucel is not recommended. However, systemic corticosteroids may be used for the management of brexucabtagene autoleucel-related adverse effects, such as cytokine release syndrome and neurological-related toxicities, without affecting the expansion and persistence of cells.', 'Others', 'The prophylactic use of systemic corticosteroids prior to the administration of brexucabtagene autoleucel is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513097, 'Brexucabtagene autoleucel', 'Trichophyton mentagrophytes', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
