INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513175, 'Chlorpromazine', 'Butabarbital', 'Moderate', 'Concomitant use of phenothiazines with barbiturates may reduce the plasma concentrations and therapeutic effects of both agents. The mechanism is unclear but may relate to metabolic induction of CYP450 hepatic enzymes. In addition, coadministration of phenothiazines and barbiturates may lead to a reduction in seizure threshold and additive central nervous system (CNS) and respiratory depressant effects.', 'DDInter', 1767317477, 'Administration of phenothiazines in patients receiving large doses of barbiturates is considered contraindicated by some authorities (US). If coadministration is required, caution as well as clinical and laboratory monitoring should be considered whenever either of these agents is added to or withdrawn from therapy. If chlorpromazine is coadministered with a barbiturate, the manufacturer of chlorpromazine recommends using 1/4 to 1/2 of the usual barbiturate dosage. Patients should be monitored for hypotension, loss of seizure control and the development of adverse effects including CNS and respiratory depression.', 'Others', 'Administration of phenothiazines in patients receiving large doses of barbiturates is considered contraindicated by some authorities (US).', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513176, 'Copanlisib', 'Butabarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of copanlisib, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of copanlisib should be considered during coadministration with CYP450 3A4 and/or P-gp inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of copanlisib should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
