INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513242, 'Calcium carbonate', 'Quinidine', 'Moderate', 'Coadministration with drugs that can increase urinary pH such as antacids, carbonic anhydrase inhibitors, or urinary alkalinizers may decrease the urinary excretion of quinidine. The proposed mechanism is increased renal tubular reabsorption due to reduced ionization of quinidine in alkaline urine.', 'DDInter', 1767317477, 'Caution is advised if quinidine is used with agents that can increase urinary pH. Quinidine levels may need to be monitored more closely following addition or discontinuation of these agents, and the quinidine dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate quinidine toxicity such as tinnitus, hearing loss, visual disturbances, diarrhea, headache, dizziness, palpitations, syncope, or irregular heartbeats.', 'Excretion', 'Caution is advised if quinidine is used with agents that can increase urinary pH.', NULL, NULL, 'Moderate', 'pharmacodynamic');
