INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513294, 'Calcium glubionate anhydrous', 'Sparfloxacin', 'Moderate', 'INTERVAL: Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics. Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc. The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 1767317477, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', 'Absorption', 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513295, 'Calcium glubionate anhydrous', 'Tiludronic acid', 'Moderate', 'INTERVAL: Products containing aluminum, calcium, magnesium and other polyvalent cations such as antacids or vitamin with mineral supplements are likely to interfere with the gastrointestinal absorption of oral bisphosphonates.', 'DDInter', 1767317477, 'Antacids or other oral medications containing aluminum, calcium, magnesium and other polyvalent cations should be administered at least 2 hours before or 2 hours after the bisphosphonate dose.', 'Absorption', 'Antacids or other oral medications containing aluminum, calcium, magnesium and other polyvalent cations should be administered at least 2 hours before or 2 hours after the bisphosphonate dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513296, 'Calcium glubionate anhydrous', 'Trichlormethiazide', 'Moderate', 'Coadministration of thiazide diuretics with high dosages of calcium and/or vitamin D has been associated with reports of hypercalcemia in some patients. Thiazide diuretics inhibit the renal excretion of calcium and may also enhance responsiveness of bone and renal tubule to parathyroid hormone, thus concurrent use of large amounts of calcium or vitamin D can lead to excessively high plasma levels of calcium. Patients who are particularly susceptible include those with hyperparathyroidism, those being treated for osteoporosis, and those receiving high dosages of vitamin D for hypoparathyroidism. Metabolic alkalosis and the milk-alkali syndrome have been reported during prolonged therapy with thiazide diuretics and calcium.', 'DDInter', 1767317477, 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider. Serum calcium should be monitored if thiazide diuretics are coadministered with high dosages of calcium and/or vitamin D. Patients should be advised to seek medical attention if they experience signs and symptoms of hypercalcemia such as dizziness, weakness, lethargy, headache, myalgia, anorexia, nausea, vomiting, and seizures.', 'Synergism', 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider.', NULL, NULL, 'Moderate', 'pharmacodynamic');
