INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513321, 'Carteolol', 'Calcium lactate', 'Moderate', 'INTERVAL: Concurrent administration with calcium salts may decrease the oral bioavailability of atenolol and possibly other beta-blockers. The exact mechanism of interaction is unknown. The investigators suggest that prolongation of the elimination half-life induced by calcium coadministration may have led to atenolol cumulation during long-term dosing, which compensated for the reduced bioavailability.', 'DDInter', 1767317477, 'It may help to separate the administration times of beta-blockers and calcium products by at least 2 hours. Patients should be monitored for potentially diminished beta-blocking effects following the addition of calcium therapy.', 'Absorption', 'It may help to separate the administration times of beta-blockers and calcium products by at least 2 hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513322, 'Cinoxacin', 'Calcium lactate', 'Moderate', 'INTERVAL: Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics. Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc. The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 1767317477, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', 'Absorption', 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513323, 'Demeclocycline', 'Calcium lactate', 'Moderate', 'INTERVAL: Administration of a tetracycline with aluminum, calcium, or magnesium salts significantly decreases tetracycline serum concentrations. The proposed mechanism is chelation of tetracycline by the cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract. The interaction has also been reported with parenteral doxycycline and oral antacids.', 'DDInter', 1767317477, 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', 'Absorption', 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513324, 'Digitoxin', 'Calcium lactate', 'Moderate', 'Excessive administration of calcium can aggravate digitalis toxicity and precipitate serious cardiac arrhythmias in digitalised patients. The mechanism probably involves additive or synergistic inotropic effects of calcium and digitalis glycosides on the myocardium.', 'DDInter', 1767317477, 'Caution is advised if calcium preparations are used in patients receiving digitalis glycosides. Patients should be advised not to exceed the calcium dosage prescribed for them and to consult their healthcare provider before consuming additional products that may contain substantial amounts of calcium (e.g., over-the-counter supplements).', 'Synergism', 'Caution is advised if calcium preparations are used in patients receiving digitalis glycosides.', NULL, NULL, 'Moderate', 'pharmacodynamic');
