INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513352, 'Alemtuzumab', 'Acetylsalicylic acid', 'Moderate', 'Coadministration of alemtuzumab with antiplatelet or anticoagulant drugs may increase the risk of bleeding. Use of alemtuzumab alone has been associated with autoimmune and hemolytic anemia, thrombocytopenia, prolonged myelosuppression, bone marrow aplasia, hemophilia, and pancytopenia. The mechanism for this interaction has not been clearly delineated, but is presumably due to additive antiplatelet/anticoagulant effects.', 'DDInter', 1767317477, 'Caution is recommended if alemtuzumab is used concomitantly with antiplatelet or anticoagulant drugs. Patients should be monitored for increased anticoagulant effects and bleeding complications. Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if alemtuzumab is used concomitantly with antiplatelet or anticoagulant drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513353, 'Frangula purshiana bark', 'Abarelix', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317477, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
