INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513559, 'Choline salicylate', 'Ardeparin', 'Moderate', 'Theoretically, salicylates may potentiate the effects of anticoagulants and increase the risk of bleeding. Salicylates interfere with the action of vitamin K and induce a dose-dependent alteration in hepatic synthesis of coagulation factors VII, IX and X, occasionally increasing the prothrombin time. While these effects are generally slight for most salicylates (except aspirin) at recommended dosages, they may be of clinical significance when combined with the inhibitory effects of anticoagulants on the clotting cascade. Moreover, salicylates are known to cause dose-related gastrointestinal bleeding, which may be complicated by anticoagulant therapy.', 'DDInter', 1767317477, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. The same precaution should be observed with the use of salicylate-related agents such as salicylamide because of their structural and pharmacological similarities.', 'Synergism', 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513560, 'Choline salicylate', 'Cinoxacin', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', 'DDInter', 1767317477, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513561, 'Clove', 'Choline salicylate', 'Minor', 'Clove and clove oil may potentiate the effects of anticoagulants and other drugs that may affect hemostasis such as platelet inhibitors, thrombolytic agents, and nonsteroidal anti-inflammatory drugs, possibly increasing the risk of bleeding. Eugenol, the active constituent of clove and clove oil, has been shown to be an inhibitor of platelet activity.', 'DDInter', 1767317477, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used clove and clove oil extensively prior to receiving treatment with anticoagulants or other drugs that may affect hemostasis, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513562, 'Danaparoid', 'Choline salicylate', 'Moderate', 'Theoretically, salicylates may potentiate the effects of anticoagulants and increase the risk of bleeding. Salicylates interfere with the action of vitamin K and induce a dose-dependent alteration in hepatic synthesis of coagulation factors VII, IX and X, occasionally increasing the prothrombin time. While these effects are generally slight for most salicylates (except aspirin) at recommended dosages, they may be of clinical significance when combined with the inhibitory effects of anticoagulants on the clotting cascade. Moreover, salicylates are known to cause dose-related gastrointestinal bleeding, which may be complicated by anticoagulant therapy.', 'DDInter', 1767317477, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. The same precaution should be observed with the use of salicylate-related agents such as salicylamide because of their structural and pharmacological similarities.', 'Synergism', 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
