INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1513568, 'Anisindione', 'Chondroitin sulfate', 'Moderate', 'A case report suggests that chondroitin and/or glucosamine may potentiate the hypoprothrombinemic effect of warfarin. The mechanism of interaction is unknown. Glucosamine is a chemical component of heparin, while chondroitin is a minor component of danaparoid and has been shown in vitro and in animals to prolong prothrombin time.', 'DDInter', 1767317477, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients treated with warfarin, the INR should be closely monitored following addition or discontinuation of chondroitin and glucosamine, and the warfarin dosage adjusted as necessary. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
