    ) VALUES (1514000, 'Diflunisal', 'Attapulgite', 'Minor', 'Concomitant administration of diflunisal and aluminum-containing products reduces the oral bioavailability of diflunisal by up to 40%. Doses of diflunisal and aluminum-containing products should be separated by at least 2 hours to ensure adequate absorption of diflunisal. Patients should be monitored for altered clinical effects of diflunisal.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
