    ) VALUES (1514033, 'Diflunisal', 'Oxazepam', 'Minor', 'One study has suggested that diflunisal may enhance the metabolism of oxazepam. The mechanism may be displacement of oxazepam from plasma protein-binding sites. If diflunisal and oxazepam must be used concurrently, close observation for evidence of altered clinical effect is recommended.', NULL, '-', NULL, '-', 'Distribution', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
