INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514304, 'Aluminum hydroxide', 'Bacampicillin', 'Moderate', 'Coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents with acid neutralizing or reducing effects may decrease the oral bioavailability and plasma concentrations of ampicillin from bacampicillin. The exact mechanism of interaction is unknown but may be related to degradation of the prodrug when gastric pH is increased.', 'DDInter', 1767317477, 'The possibility of a reduced or subtherapeutic response to bacampicillin should be considered during coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH. Preferably, these agents should be avoided during therapy with bacampicillin, or an alternative antibiotic be prescribed if these medications cannot be discontinued. Patients treated with antacids (or oral medications that contain antacids such as didanosine buffered tablets or pediatric oral solution) may minimize the effects of the interaction with bacampicillin by separating the times of administration by at least 2 hours.', 'Absorption', 'The possibility of a reduced or subtherapeutic response to bacampicillin should be considered during coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514305, 'Bendroflumethiazide', 'Magnesium hydroxide', 'Moderate', 'The chronic use or abuse of laxatives may potentiate the pharmacologic effects of diuretics. Laxatives can cause significant losses of fluid and electrolytes, including sodium, potassium, magnesium and zinc, and these effects may be additive to those of diuretics.', 'DDInter', 1767317477, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant use with diuretics, patients should be advised to contact their physician if they experience signs and symptoms of fluid and electrolyte depletion such as dizziness, lightheadedness, dry mouth, thirst, fatigue, weakness, lethargy, muscle cramps, decreased urination, postural hypotension, and tachycardia. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Synergism', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514306, 'Benzthiazide', 'Magnesium hydroxide', 'Moderate', 'The chronic use or abuse of laxatives may potentiate the pharmacologic effects of diuretics. Laxatives can cause significant losses of fluid and electrolytes, including sodium, potassium, magnesium and zinc, and these effects may be additive to those of diuretics.', 'DDInter', 1767317477, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant use with diuretics, patients should be advised to contact their physician if they experience signs and symptoms of fluid and electrolyte depletion such as dizziness, lightheadedness, dry mouth, thirst, fatigue, weakness, lethargy, muscle cramps, decreased urination, postural hypotension, and tachycardia. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Synergism', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', NULL, NULL, 'Moderate', 'pharmacodynamic');
