INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514308, 'Aluminum hydroxide', 'Cabotegravir', 'Moderate', 'INTERVAL: Coadministration with antacids may decrease the bioavailability of oral cabotegravir. The proposed mechanism likely involves a pH-dependent reduction in dissolution and absorption of cabotegravir, which is poorly soluble over a wide pH range. The interaction has not been studied specifically with antacids.', 'DDInter', 1767317477, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of cabotegravir, antacids or other medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) should be administered at least 2 hours before or 4 hours after the oral cabotegravir dose.', 'Absorption', 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of cabotegravir, antacids or other medications that contain antacids (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
