INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514337, 'Fludeoxyglucose (18F)', 'Microcrystalline cellulose', 'Moderate', 'Certain medications may interfere with the results of diagnostic studies using fludeoxyglucose F18 by increasing or decreasing uptake of the agent into tissues.', 'DDInter', 1767317477, 'Clinicians should be aware of possible diagnostic interference associated with the following medications: colony-stimulating factors may increase uptake in spleen and bone marrow; glucose-induced hyperglycemia may interfere with uptake; dopamine and insulin may decrease myocardial and tumor cell uptake; laxative-induced intestinal muscle activity and/or irritation may increase intestinal uptake.', 'Absorption', 'Clinicians should be aware of possible diagnostic interference associated with the following medications: colony-stimulating factors may increase uptake in spleen and bone marrow; glucose-induced hyperglycemia may interfere with uptake; dopamine and insulin may decrease myocardial and tumor cell uptake; laxative-induced intestinal muscle activity and/or irritation may increase intestinal uptake.', NULL, NULL, 'Moderate', 'pharmacodynamic');
