INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514473, 'Fosnetupitant', 'Darifenacin', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of darifenacin, which is a substrate of these isoenzymes.', 'DDInter', 1767317477, 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary. Patients should be advised to contact their physician if they experience undue adverse effects of darifenacin such as severe abdominal pain or constipation for 3 or more days.', 'Metabolism', 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514474, 'Fosnetupitant', 'Darolutamide', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of darolutamide, which is a substrate of both the isoenzyme and the efflux transporter. Caution is advised when darolutamide is used with CYP450 3A4 or P-gp inhibitors. Patients should be monitored more frequently for adverse effects such as fatigue, hypertension, neutropenia and abnormal liver function tests, and the darolutamide dosage adjusted as necessary in accordance with the product labeling.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
