INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514476, 'Fosnetupitant', 'Deflazacort', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of 21-desdeflazacort, the active metabolite of deflazacort that is formed by esterases after oral administration and further metabolized by CYP450 3A4 to several inactive metabolites.', 'DDInter', 1767317477, 'The manufacturer suggests using one-third of the recommended dosage of deflazacort when coadministered with potent or moderate CYP450 3A4 inhibitors. Some authorities recommend avoiding concomitant use of deflazacort and CYP450 3A inhibitors unless the benefit outweighs the risk, in which case patients should be monitored for systemic corticosteroid side effects.', 'Metabolism', 'The manufacturer suggests using one-third of the recommended dosage of deflazacort when coadministered with potent or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514477, 'Fosnetupitant', 'Delavirdine', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of netupitant. When a single 300 mg-0.5 mg dose of netupitant-palonosetron was administered following 400 mg once daily administration of the potent CYP450 3A4 inhibitor ketoconazole for 12 days, mean netupitant peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 25% and 140%, respectively, while mean Cmax and AUC of palonosetron increased by 15% and 10%, respectively. No dosage adjustment is necessary for single-dose administration of netupitant-palonosetron when used with potent CYP450 3A4 inhibitors.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514478, 'Fosnetupitant', 'Dexamethasone', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may significantly increase the plasma concentrations of dexamethasone. The proposed mechanism is netupitant inhibition of dexamethasone metabolism via CYP450 3A4.', 'DDInter', 1767317477, 'Dexamethasone should be administered at a reduced dosage when used with netupitant or fosnetupitant. For the prevention of acute and delayed nausea and vomiting associated with initial and repeat courses of cancer chemotherapy, dexamethasone should be dosed according to the product labeling for netupitant-palonosetron or fosnetupitant-palonosetron.', 'Metabolism', 'Dexamethasone should be administered at a reduced dosage when used with netupitant or fosnetupitant.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514479, 'Fosnetupitant', 'Diazepam', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy. The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Synergism', 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514480, 'Fosnetupitant', 'Dienestrol (topical)', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy. The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Synergism', 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514481, 'Fosnetupitant', 'Dihydroergotamine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors.', 'DDInter', 1767317477, 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors. Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.', 'Metabolism', 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514482, 'Fosnetupitant', 'Diltiazem', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy. The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Synergism', 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514483, 'Fosnetupitant', 'Docetaxel', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 or dual CYP450 3A4 and P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of docetaxel, which is a substrate of both CYP450 3A4 and P-gp.', 'DDInter', 1767317477, 'Caution is advised if docetaxel is prescribed in combination with CYP450 3A4 inhibitors or dual CYP450 3A4 and P-gp inhibitors. Patients should be closely monitored for the development of docetaxel toxicity such as myelosuppression, stomatitis, neurotoxicity (e.g., paresthesia, dysesthesia, pain), myalgia, asthenia, fluid retention, nausea, vomiting, and diarrhea. Dosage reduction of docetaxel may be required if an interaction is suspected.', 'Metabolism', 'Caution is advised if docetaxel is prescribed in combination with CYP450 3A4 inhibitors or dual CYP450 3A4 and P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514484, 'Fosnetupitant', 'Doravirine', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514485, 'Fosnetupitant', 'Dutasteride', 'Moderate', 'Based on in vitro data, coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of dutasteride, which is metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The possibility of prolonged and/or increased pharmacologic effects of dutasteride should be considered during concomitant therapy with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Metabolism', 'The possibility of prolonged and/or increased pharmacologic effects of dutasteride should be considered during concomitant therapy with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514486, 'Fosnetupitant', 'Duvelisib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of duvelisib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised when duvelisib is used with CYP450 3A4 inhibitors. Patients should be monitored for the development of toxicities such as diarrhea, colitis, neutropenia, thrombocytopenia, pneumonitis, infections, cutaneous reactions and hepatotoxicity, and the dosing adjusted or discontinued in accordance with the duvelisib product labeling.', 'Metabolism', 'Caution is advised when duvelisib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514487, 'Fosnetupitant', 'Echinacea', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 1767317477, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514488, 'Fosnetupitant', 'Elagolix', 'Moderate', 'Coadministration with inhibitors of the OATP 1B1 hepatic uptake transporter, CYP450 3A4 metabolic isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of elagolix. The effect of P-gp inhibitors on the pharmacokinetics of elagolix has not been studied, but some increases in elagolix exposure may occur, as it is a P-gp substrate. Increased exposure to elagolix may increase the risk of serious adverse effects such as bone loss, suicidal ideation and behavior, exacerbation of mood disorders, and hepatic transaminase elevations.', 'DDInter', 1767317477, 'Caution is advised when elagolix is used with OATP 1B1, CYP450 3A4, and/or P-gp inhibitors. Patients should be monitored for potentially altered effects of elagolix following the initiation or discontinuation of these inhibitors, and the elagolix dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when elagolix is used with OATP 1B1, CYP450 3A4, and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514489, 'Fosnetupitant', 'Eliglustat', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317477, 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs). In the absence of a concomitant CYP450 2D6 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to EMs treated with a moderate CYP450 3A4 inhibitor. However, eliglustat should not be used with a moderate CYP450 3A4 inhibitor in CYP450 2D6 IMs or poor metabolizers (PMs). Moderate CYP450 3A4 inhibitors include aprepitant, ciprofloxacin, clotrimazole, crizotinib, darunavir, diltiazem, dronedarone, fluconazole, fusidic acid, imatinib, isavuconazonium, miconazole, mifepristone, netupitant, quinupristin-dalfopristin, ranolazine, stiripentol, and verapamil. Potent and moderate CYP450 2D6 inhibitors include abiraterone, bupropion, celecoxib, cimetidine, cinacalcet, clobazam, darifenacin, diphenhydramine, duloxetine, fluoxetine, methotrimeprazine, mirabegron, paroxetine, propoxyphene, quinidine, ranolazine, sertraline, stiripentol, and terbinafine.', 'Metabolism', 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs).', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514490, 'Fosnetupitant', 'Encorafenib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme. Increased exposure to encorafenib may increase the risk of serious and life-threatening adverse effects such as hemorrhage, uveitis, QT prolongation, hepatotoxicity, dermatologic reactions, and new malignancies.', 'DDInter', 1767317477, 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the encorafenib dose to one-half of the dose used prior to addition of the moderate CYP450 3A4 inhibitor. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the encorafenib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514491, 'Fosnetupitant', 'Enfortumab vedotin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of enfortumab vedotin. Enfortumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when enfortumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development or exacerbation of toxicities such as peripheral neuropathy, hyperglycaemia, dermatologic reactions (maculopapular rash, pruritus, symmetrical drug-related intertriginous and flexural exanthema (SDRIFE), bullous dermatitis, exfoliative dermatitis, palmar-plantar erythrodysesthesia), and ocular disorders (dry eyes, keratitis, blurred vision, limbal stem cell deficiency), and the dosing of enfortumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when enfortumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514492, 'Fosnetupitant', 'Entrectinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', 'DDInter', 1767317477, 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible. If coadministration is necessary, the dosage of entrectinib should be reduced to 100 mg once daily for adults and pediatrics patients (12 years and older) with a body surface area (BSA) greater than 1.5 m2. Following discontinuation, and after an appropriate washout period of the CYP450 3A4 inhibitor (i.e., 3 to 5 half-lives), the entrectinib dosage taken prior to initiating the CYP450 3A4 inhibitor may be resumed. For pediatric patients with a body surface area of 1.5 m2 or less, concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided.', 'Metabolism', 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514493, 'Fosnetupitant', 'Enzalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of netupitant.', 'DDInter', 1767317477, 'Concomitant use of netupitant or its prodrug, fosnetupitant, should generally be avoided during chronic administration of potent CYP450 3A4 inducers due to the potential for reduced efficacy of netupitant.', 'Metabolism', 'Concomitant use of netupitant or its prodrug, fosnetupitant, should generally be avoided during chronic administration of potent CYP450 3A4 inducers due to the potential for reduced efficacy of netupitant.', NULL, NULL, 'Major', 'pharmacodynamic');
