INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514567, 'Fosnetupitant', 'Mitotane', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of netupitant.', 'DDInter', 1767317477, 'Concomitant use of netupitant or its prodrug, fosnetupitant, should generally be avoided during chronic administration of potent CYP450 3A4 inducers due to the potential for reduced efficacy of netupitant.', 'Metabolism', 'Concomitant use of netupitant or its prodrug, fosnetupitant, should generally be avoided during chronic administration of potent CYP450 3A4 inducers due to the potential for reduced efficacy of netupitant.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514568, 'Fosnetupitant', 'Modafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme. Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4. The clinical significance of this potential interaction is unknown. Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514569, 'Fosnetupitant', 'Naldemedine', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G. Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.', 'DDInter', 1767317477, 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp). Patients should be closely monitored for potential opioid withdrawal symptoms as well as other adverse effects of naldemedine.', 'Metabolism', 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514570, 'Fosnetupitant', 'Naloxegol', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of naloxegol, which is primarily metabolized by the isoenzyme. Increased exposure to naloxegol may precipitate opioid withdrawal symptoms such as hyperhidrosis, lacrimation, rhinorrhea, chills, diarrhea, abdominal pain, anxiety, insomnia, irritability, restlessness, and yawning.', 'DDInter', 1767317477, 'Concomitant use of naloxegol with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the dosage of naloxegol should be reduced to 12.5 mg once daily and the patient closely monitored for opioid withdrawal symptoms.', 'Metabolism', 'Concomitant use of naloxegol with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514571, 'Fosnetupitant', 'Nelfinavir', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of netupitant. When a single 300 mg-0.5 mg dose of netupitant-palonosetron was administered following 400 mg once daily administration of the potent CYP450 3A4 inhibitor ketoconazole for 12 days, mean netupitant peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 25% and 140%, respectively, while mean Cmax and AUC of palonosetron increased by 15% and 10%, respectively. No dosage adjustment is necessary for single-dose administration of netupitant-palonosetron when used with potent CYP450 3A4 inhibitors.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514572, 'Fosnetupitant', 'Neratinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.', 'Metabolism', 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514573, 'Fosnetupitant', 'Nevirapine', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy. The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Synergism', 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
