INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514732, 'Gatifloxacin', 'Lactobacillus acidophilus', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', 'DDInter', 1767317477, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
