INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514797, 'Gentamicin', 'Doxacurium', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', 'DDInter', 1767317477, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed. If these agents are used concurrently, vital signs should be closely monitored and drug dosages adjusted accordingly. In addition, ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514798, 'Indium In-111 oxyquinoline', 'Gentamicin', 'Moderate', 'Certain medications may decrease chemotaxis and cause false-negative results with Indium In-111-labeled leukocyte studies.', 'DDInter', 1767317477, 'Clinicians should be aware of possible diagnostic interference in patients who are on long-term antibiotic treatment, corticosteroids, interleukin-2, parenteral nutrition, or high doses or supratherapeutic concentrations of lidocaine or procainamide.', 'Others', 'Clinicians should be aware of possible diagnostic interference in patients who are on long-term antibiotic treatment, corticosteroids, interleukin-2, parenteral nutrition, or high doses or supratherapeutic concentrations of lidocaine or procainamide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514799, 'Iodipamide', 'Gentamicin', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', 'DDInter', 1767317477, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', NULL, NULL, 'Major', 'pharmacodynamic');
