INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514898, 'Ginseng', 'Digitoxin', 'Moderate', 'Asian and Siberian ginseng may interfere with digoxin either pharmacodynamically or with its monitoring via certain immunoassays. The interaction has been observed in vitro as well as in nondigoxin-treated mice that were fed ginseng and is thought to be due to substances in the ginseng that structurally resemble cardiac glycosides. Serum pools prepared from samples from patients receiving digoxin and then supplemented with Asian or Siberian ginseng also demonstrated interference.', 'DDInter', 1767317477, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Use of ginseng should probably be avoided in patients treated with cardiac glycosides.', 'Others', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
