INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514914, 'Calcium glucoheptonate', 'Bepridil', 'Moderate', 'Calcium-containing products may decrease the effectiveness of calcium channel blockers by saturating calcium channels with calcium. Calcium chloride has been used to manage acute severe verapamil toxicity.', 'DDInter', 1767317477, 'Management consists of monitoring the effectiveness of calcium channel blocker therapy during coadministration with calcium products.', 'Antagonism', 'Management consists of monitoring the effectiveness of calcium channel blocker therapy during coadministration with calcium products.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514915, 'Digitoxin', 'Calcium glucoheptonate', 'Major', 'The administration of parenteral calcium preparations, particularly by rapid IV injection, may precipitate serious cardiac arrhythmias in digitalized patients. The mechanism is probably related to the additive or synergistic inotropic effects of calcium and digitalis glycosides on the myocardium.', 'DDInter', 1767317477, 'Intravenous calcium should preferably not be used in patients receiving digitalis glycosides, or it should be given slowly or in small amounts to avoid high serum calcium concentrations. Patients should be monitored for development of arrhythmias.', 'Synergism', 'Intravenous calcium should preferably not be used in patients receiving digitalis glycosides, or it should be given slowly or in small amounts to avoid high serum calcium concentrations.', NULL, NULL, 'Major', 'pharmacodynamic');
