INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514964, 'Halothane', 'Dexmethylphenidate', 'Moderate', 'The administration of volatile general anesthetics during therapy with methylphenidate or its derivatives may increase the risk of hypertension and/or arrhythmias during surgery. The proposed mechanism is anesthetic-induced myocardial sensitization to the stimulation caused by methylphenidate.', 'DDInter', 1767317477, 'When surgery is planned, it is recommended that methylphenidate or its derivatives should not be taken on the day of surgery.', 'Others', 'When surgery is planned, it is recommended that methylphenidate or its derivatives should not be taken on the day of surgery.', NULL, NULL, 'Moderate', 'pharmacodynamic');
